import json

# 統計所有品項總數
def summarize_clothing_counts(records):
    item_counts = {}

    for record in records:
        clothing_counts = record['data'].get('clothing_counts', {})
        for item, count in clothing_counts.items():
            if item not in item_counts:
                item_counts[item] = 0
            item_counts[item] += count

    # 整理成列表並依數量排序
    item_count_list = [{"item": item, "count": count} for item, count in sorted(item_counts.items(), key=lambda x: x[1], reverse=True)]
    
    return item_count_list

